pragma solidity ^0.8.13;

import {Proof, ProofLib} from "./Proof.sol";

import {IProofVerifier} from "./proof_verifier/IProofVerifier.sol";
import {ProofVerifierFactory} from "./proof_verifier/ProofVerifierFactory.sol";
import {CallAssumptionsLib} from "./CallAssumptions.sol";

abstract contract Verifier {
    uint256 private constant SELECTOR_LEN = 4;
    uint256 private constant PROOF_OFFSET = SELECTOR_LEN;
    uint256 private constant CALL_ASSUMPTIONS_BEGIN = PROOF_OFFSET + ProofLib.CALL_ASSUMPTIONS_OFFSET;
    uint256 private constant CALL_ASSUMPTIONS_END =
        CALL_ASSUMPTIONS_BEGIN + CallAssumptionsLib.CALL_ASSUMPTIONS_ENCODING_LENGTH;

    IProofVerifier public verifier;

    constructor() {
        verifier = ProofVerifierFactory.produce();
    }

    modifier onlyVerified(address prover, bytes4 selector) {
        _verify(prover, selector);
        _;
    }

    function _verify(address prover, bytes4 selector) internal view {
        (Proof memory proof, bytes32 journalHash) = _decodeCalldata();
        verifier.verify(proof, journalHash, prover, selector);
    }

    function _decodeCalldata() private pure returns (Proof memory, bytes32) {
        Proof memory proof = abi.decode(msg.data[PROOF_OFFSET:], (Proof));

        uint256 paramsBegin = CALL_ASSUMPTIONS_END;
        uint256 paramsLen =
            proof.length - ProofLib.PROOF_ENCODING_LENGTH - CallAssumptionsLib.CALL_ASSUMPTIONS_ENCODING_LENGTH;
        uint256 paramsEnd = paramsBegin + paramsLen;

        bytes memory callAssumptions = msg.data[CALL_ASSUMPTIONS_BEGIN:CALL_ASSUMPTIONS_END];
        bytes memory params = msg.data[paramsBegin:paramsEnd];

        bytes memory journalWithEmptyProof = bytes.concat(callAssumptions, abi.encode(ProofLib.emptyProof()), params);
        bytes32 journalHash = sha256(journalWithEmptyProof);

        return (proof, journalHash);
    }

    function shiftOffset(bytes memory data, uint256 shiftBy, uint256 offsetPosition)
        public
        pure
        returns (bytes memory)
    {
        uint256 offsetPositionRelativeToJournal = CallAssumptionsLib.CALL_ASSUMPTIONS_ENCODING_LENGTH + offsetPosition;

        require(data.length >= offsetPositionRelativeToJournal, "Encoded data too short");

        uint256 dataOffset;
        assembly {
            dataOffset := mload(add(data, offsetPositionRelativeToJournal))
        }

        uint256 shiftedOffset = dataOffset - shiftBy;

        bytes memory dataCopy = data;

        assembly {
            mstore(add(dataCopy, offsetPositionRelativeToJournal), shiftedOffset)
        }

        return dataCopy;
    }
}
","keccak256":"0x5b3b76f9a7f26880407db7860c8dfff5afb6e71cd73821a19a1146b74f38370c","license":"UNLICENSED"},"vlayer-0.1.0/WebProof.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.13;

import {Strings} from "@openzeppelin-contracts-5.0.1/utils/Strings.sol";
import {Address} from "@openzeppelin-contracts-5.0.1/utils/Address.sol";

import {Precompiles} from "./PrecompilesAddresses.sol";

struct WebProof {
    string webProofJson;
}

struct Web {
    string body;
    string notaryPubKey;
}

library WebProofLib {
    using Strings for string;

    string private constant NOTARY_PUB_KEY =
        "-----BEGIN PUBLIC KEY-----\nMFkwEwYHKoZIzj0CAQYIKoZIzj0DAQcDQgAExpX/4R4z40gI6C/j9zAM39u58LJu\n3Cx5tXTuqhhu/tirnBi5GniMmspOTEsps4ANnPLpMmMSfhJ+IFHbc3qVOA==\n-----END PUBLIC KEY-----\n";

    function verify(WebProof memory webProof, string memory dataUrl) internal view returns (Web memory) {
        Web memory web = recover(webProof, dataUrl);

        require(NOTARY_PUB_KEY.equal(web.notaryPubKey), "Incorrect notary public key");

        return web;
    }

    function recover(WebProof memory webProof, string memory dataUrl) internal view returns (Web memory) {
        (bool success, bytes memory returnData) =
            Precompiles.VERIFY_AND_PARSE_PRECOMPILE.staticcall(bytes(webProof.webProofJson));

        Address.verifyCallResult(success, returnData);

        string[4] memory data = abi.decode(returnData, (string[4]));

        require(dataUrl.equal(data[0]), "Incorrect URL");

        return Web(data[2], data[3]);
    }
}

library WebLib {
    address private constant JSON_GET_STRING_PRECOMPILE = address(0x102);
    address private constant JSON_GET_INT_PRECOMPILE = address(0x103);
    address private constant JSON_GET_BOOL_PRECOMPILE = address(0x104);
    address private constant JSON_GET_ARRAY_LENGTH = address(0x105);

    function jsonGetString(Web memory web, string memory jsonPath) internal view returns (string memory) {
        require(bytes(web.body).length > 0, "Body is empty");

        bytes memory encodedParams = abi.encode([web.body, jsonPath]);
        (bool success, bytes memory returnData) = JSON_GET_STRING_PRECOMPILE.staticcall(encodedParams);
        Address.verifyCallResult(success, returnData);

        return abi.decode(returnData, (string));
    }

    function jsonGetInt(Web memory web, string memory jsonPath) internal view returns (int256) {
        require(bytes(web.body).length > 0, "Body is empty");

        bytes memory encodedParams = abi.encode([web.body, jsonPath]);
        (bool success, bytes memory returnData) = JSON_GET_INT_PRECOMPILE.staticcall(encodedParams);
        Address.verifyCallResult(success, returnData);

        return abi.decode(returnData, (int256));
    }

    function jsonGetBool(Web memory web, string memory jsonPath) internal view returns (bool) {
        require(bytes(web.body).length > 0, "Body is empty");

        bytes memory encodedParams = abi.encode([web.body, jsonPath]);
        (bool success, bytes memory returnData) = JSON_GET_BOOL_PRECOMPILE.staticcall(encodedParams);
        Address.verifyCallResult(success, returnData);

        return abi.decode(returnData, (bool));
    }

    function jsonGetArrayLength(Web memory web, string memory jsonPath) internal view returns (uint256) {
        require(bytes(web.body).length > 0, "Body is empty");

        bytes memory encodedParams = abi.encode([web.body, jsonPath]);
        (bool success, bytes memory returnData) = JSON_GET_ARRAY_LENGTH.staticcall(encodedParams);
        Address.verifyCallResult(success, returnData);

        return abi.decode(returnData, (uint256));
    }
}
